export const TodoElement = ({ todo, removeFromList, toggleCheckBox }) => {

  const handleRemoveElement = () => {
    removeFromList(todo.id)
  }

  const handleToggleCheckBox = () => {
    toggleCheckBox(todo.id)
  }
  return (
    <div className="element">
      <input className="checkbox" type={'checkbox'} onClick = {() => handleToggleCheckBox()} />
      <h1>{todo.title}</h1>
      <input className="submit" onClick={() => handleRemoveElement()} type={'button'} value={'Remove'}></input>
      <img className="trash" alt="remove"/>
    </div>
  )
}
