import { Header } from "./components/Header";
import { useState } from "react";
import { List } from "./components/List";
import './components/header.css'
import './components/list.css'
import './components/index.css'

function App() {
  const [todoList, setTodo] = useState([]);

  const addToList = (newItem) => {
    setTodo((lastElem) => {
      return [...lastElem, newItem]
    })
  }

  const removeFromList = (id) => {
    const copyOfMyTodos = todoList.filter(todo => id !== todo.id);
    setTodo(copyOfMyTodos);
  }

  const toggleCheckBox = (id) => {
    const changeableTodo = todoList.find((todo) => todo.id === id);
    changeableTodo.finished = !changeableTodo.finished;
  }

  return (
    <div className="node">
      <div className="container">
        <Header addToList={addToList} />
        <List todoList={todoList} removeFromList={(id) => removeFromList(id)} toggleCheckBox={(id) => toggleCheckBox(id)} />
      </div>
    </div>
  );
}

export default App;
