import { useRef, useState } from "react";

export const TodoList = () => {
    const [todo, setTodo] = useState();

    const todoNameRef = useRef();

    const addNewTodo = (e) => {
        const newText = todoNameRef.current.value;
        const newID = new Date().getTime();
        if (newText === '') {
            return;
        }
        setTodo(lastTodo => {
            return [...lastTodo, {id: newID, text: newText, done: false}]
        })
        todoNameRef.current.value = null;
    }


    return (
        <div>
            <input type={'text'} ref={todoNameRef}></input>
            <input className="btn" onClick={addNewTodo} type={'button'} value={'Add new TODO'}></input>
        </div>
    )
}
