import { useState } from "react";
import addTodo from '../images/add.png'

export const Header = ({ addToList }) => {

    const [item, setItem] = useState({});

    const getNewTitle = (e) => {

        setItem({
            id: new Date().getTime(),
            title: e.target.value,
            finished: false,
        });
    }

    const addItem = () => {
        if (item.title === undefined) {
            return;
        }
        addToList(item);
        setItem({});
    }

    return (
        <div className="header">
            <div className="header-left">
                <input placeholder="Enter todo..." value={item?.title || ''} onChange={(e) => getNewTitle(e)} type='text' />
            </div>
            <div className="header-right">
                <button onClick={() => addItem()}><img className="add" src={addTodo} alt= 'add'/></button>
            </div>
        </div>
    )
}
