import trash from '../images/trash.jpg'
import checked from '../images/checked.png'
export const TodoElement = ({ todo, removeFromList, toggleCheckBox }) => {

  const handleRemoveElement = () => {
    removeFromList(todo.id)
  }

  const handleToggleCheckBox = () => {
    toggleCheckBox(todo.id)
  }
  return (
    <div className="element">
      <div className='element-left'>
        <img src={checked} className="checked" type={'checkbox'} onClick={() => handleToggleCheckBox()} />
        <h1>{todo.title}</h1>
      </div>
      <div className='element-right'>
        <img className="trash" src={trash} alt="remove" onClick={() => handleRemoveElement()} />
      </div>
    </div>
  )
}
