import { useState } from "react";

export const Header = ({ addToList }) => {

    const [item, setItem] = useState({});

    const getNewTitle = (e) => {
        if (newTitle === '') {
            return;
        }
        if(e.target.value === null){
            return;
        }

        setItem({
            id: new Date().getTime(),
            title: e.target.value,
            finished: false,
        });
    }

    const addItem = () => {
        addToList(item);
        setItem({});
    }

    return (
        <div>
            <input value={item?.title || ''} onChange={(e) => getNewTitle(e)} type='text' />
            <button onClick={() => addItem()}>Add</button>
        </div>
    )
}
