import { useState } from "react";

export const Header = ({ addToList }) => {

    const [newItem, setNewItem] = useState({});

    const getNewTitle = (e) => {
        // if (newTitle === '') {
        //     return;
        // }

        setNewItem({
            id: new Date().getTime(),
            title: e.target.value,
            finished: false,
        });
    }

    const addItem = () => {
        addToList(newItem)
    }

    return (
        <div>
            <input onChange={(e) => getNewTitle(e)} type='text' />
            <button onClick={() => addItem()}>Add</button>
        </div>
    )
}
